<?php
$message = "";

/**
 * @param string $message
 * @param int $length
 * @return int|null
 */
function decode(string $message, int $length) : ?int
{
    $stack = [];
    foreach (str_split($message) as $pos => $char)
    {
        $stack[] = $char;

        if (count($stack) === $length)
        {
            if (count(array_unique($stack)) === $length)
            {
                return ($pos + 1);
            }

            array_shift($stack);
        }
    }

    return null;
}

echo "Part 1: " . decode($message, 4) . "<br>";
echo "Part 2: " . decode($message, 14);